<template>
    <el-collapse class="collapse" v-model="activeNames">
        <el-collapse-item :title="item.title" :name="idx" v-for="(item, idx) in collapse" :key="item.title">
            <div class="video-container">
                <video controls poster="data:image/jpeg;base64,">
                    <source :src="`videos/${item.fileName}.Ogg`" type="video/ogg">
                    <source :src="`videos/${item.fileName}.mp4`" type="video/mp4">
                </video>
            </div>
        </el-collapse-item>
    </el-collapse>
</template>

<script>
module.exports = {
    props: {
        collapse: {
            type: Array,
            required: true
        },
        firstActive: {
            type: Boolean,
            default: true
        }
    },
    data() {
        return {
            activeNames: [] // 展開的面板
        };
    },
    computed: {
        videos() {
            return Array.from(this.$el.querySelectorAll('video'));
        },
        inactiveNames() { // 收起的面板
            let videoIdx = this.videos.map((el, idx) => idx);
            for (const key in this.activeNames) {
                if (this.activeNames.hasOwnProperty(key)) {
                    const element = this.activeNames[key];
                    videoIdx = _.without(videoIdx, element); // 將所有影片的索引值移除展開的面板索引值
                }
            }
            return videoIdx;
        }
    },
    watch: {
        activeNames() {
            // 當摺疊面板時，暫停已隱藏的影片
            this.inactiveNames.forEach(el => {
                if (!this.videos[el].paused) {
                    this.videos[el].pause();
                }
            });
        }
    },
    created() {
        if (this.firstActive) {
            this.activeNames.push(0); // 設定預設展開第一項
        }
    },
}
</script>

